mapper.select:
  
  select p.postid, u.email, u.u_name, p.title, p.p_content, p.imgpath
	    from bmt_post p, bmt_user u
	where u.email = p.email and p.postid=#{postId}

  
  select postid, boardid, email, title, imgpath, p_content
	    from bmt_post
	where postid=#{postId}

  select p.postid, u.email, u.u_name, p.title, p.p_content, p.imgpath
	    from bmt_post p, bmt_user u
	where u.email = p.email and p.postid=#{postId}
